college_code={'S':'理學院','E':'教育學院', 'L':'文學院','A':'運休學院','M':'音樂學院','I':'國社學院','H':'科技學院','T':'藝術學院','O':'管理學院'}
soup = BeautifulSoup(html_content, 'html.parser')
data=[]
for item in soup.find_all('li', class_='x-boundlist-item'):
    text = item.get_text(strip=True)
    if '系（學）' in text or '學程（學）' in text:
        code, name = text.split(maxsplit=1)
        name=name.replace('（學）','')
        data.append([code[0],college_code[code[0]],code[2:4], name])
df = pd.DataFrame(data, columns=['College Code','College Name','Department Code', 'Department Name'])
print(df)
df.to_csv(r'build_id_and_password_data\department_codes.csv', index=False, header=True, encoding='utf-8-sig')

